import React, { useEffect, useState } from 'react';

export default function DiscountSettings() {
  const [months, setMonths] = useState([]);
  const [discount, setDiscount] = useState(null); // null initially to handle empty value
  const [message, setMessage] = useState('');
  const [isNewDiscount, setIsNewDiscount] = useState(false);
  const API_URL = 'http://localhost:5003/admin/discount';

  // Fetch discount settings on mount
  useEffect(() => {
    fetchDiscountSettings();
  }, []);

  const fetchDiscountSettings = async () => {
    try {
      const res = await fetch(API_URL);
      const data = await res.json();
      console.log("Fetched Discount Settings:", data); // Debugging log
      if (data.message) {
        setMessage(data.message); // No discount set yet
      } else {
        setMonths(data.months || []);
        setDiscount(data.discount || null);
      }
    } catch (err) {
      console.error("Error fetching discount settings:", err);
      setMessage('Error fetching discount settings.');
    }
  };

  const handleInputChange = (field, value) => {
  console.log(`Input Change - Field: ${field}, Value: ${value}`); // Debugging log

  if (field === 'months') {
    // Split by comma, trim spaces, and filter out any invalid entries
    const monthArray = value
      .split(',')
      .map(month => month.trim())
      .filter(month => {
        const num = parseInt(month, 10);
        return !isNaN(num) && num >= 1 && num <= 12; // Ensure valid month
      });

    console.log('Valid months:', monthArray); // Debugging log
    setMonths(monthArray); // Update state with valid months
  } else if (field === 'discount') {
    const discountValue = value === "" ? null : Number(value);
    console.log(`Setting discount: ${discountValue}`); // Debugging log
    setDiscount(discountValue); // Set discount to null if empty
  }
};


  const handleSaveDiscount = async () => {
    console.log("Saving Discount - Months:", months, "Discount:", discount); // Debugging log
    if (!months.length || discount === null) {
      alert('All fields are required');
      return;
    }

    const discountData = {
      months,
      discount: Number(discount),
    };

    try {
      const res = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(discountData),
      });

      const data = await res.json();
      console.log("Response from Saving Discount:", data); // Debugging log
      if (res.ok) {
        setMessage(data.message);
        setMonths(data.months);
        setDiscount(data.discount);
        setIsNewDiscount(false);
      } else {
        setMessage(data.error || 'Failed to update discount');
      }
    } catch (err) {
      console.error('Error saving discount:', err);
      setMessage('Error saving discount');
    }
  };

  const handleDeleteDiscount = async () => {
    console.log("Deleting Discount..."); // Debugging log
    try {
      const res = await fetch(API_URL, { method: 'DELETE' });
      if (res.ok) {
        setMessage('Discount deleted successfully');
        setMonths([]);  // Reset the months to empty array
        setDiscount(null);  // Reset the discount to null (empty state)
      } else {
        setMessage('Failed to delete discount');
      }
    } catch (err) {
      console.error('Error deleting discount:', err);
      setMessage('Error deleting discount');
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6 text-center">Discount Settings</h2>

      {message && <div className="mb-4 text-gray-700">{message}</div>}

      {/* Discount Form */}
      <div className="flex flex-wrap gap-4 mb-6">
        <input
          type="text"
          placeholder="Seasonal Discount Months (comma separated)"
          value={months.join(', ')}
          onChange={(e) => handleInputChange('months', e.target.value)}
          className="border p-2 rounded w-1/2"
        />
        <input
          type="number"
          placeholder="Discount (%)"
          value={discount !== null ? discount : ''}  // If discount is null, show an empty input
          onChange={(e) => handleInputChange('discount', e.target.value)}
          className="border p-2 rounded w-1/2"
        />
        <button
          onClick={handleSaveDiscount}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          {isNewDiscount ? 'Add Discount' : 'Save Changes'}
        </button>
        {months.length > 0 && discount !== null && (
          <button
            onClick={handleDeleteDiscount}
            className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
          >
            Delete Discount
          </button>
        )}
      </div>

      {/* Existing Discount Display */}
      {months.length > 0 && discount !== null && (
        <div className="mt-6">
          <h3 className="text-xl font-semibold">Current Seasonal Discount</h3>
          <p><strong>Months:</strong> {months.join(', ')}</p>
          <p><strong>Discount:</strong> {discount}%</p>
        </div>
      )}
    </div>
  );
}
