import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../AuthContext';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [birthdayMessage, setBirthdayMessage] = useState('');
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);

  const handleLogin = async (e) => {
    e.preventDefault();

    const response = await fetch(`http://localhost:5005/login`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password })
    });

    const data = await response.json();

    if (response.ok) {
      // Save username to localStorage
      localStorage.setItem("username", username);

      // ðŸ”¥ Fix: store username and role at root level, not nested in role
      login({ username, role: data.userType });

      // Check for birthday wish after login
      try {
        const birthdayRes = await fetch('http://localhost:5005/user/birthday/wish', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ user_id: username }) // Assuming username is used as the user_id
        });

        const birthdayData = await birthdayRes.json();

        if (birthdayData.birthday_wish) {
          setBirthdayMessage(birthdayData.birthday_wish); // Show birthday message
        } else {
          setBirthdayMessage(""); // No birthday message if it's not the user's birthday
        }
      } catch (err) {
        console.error('Error fetching birthday wish:', err);
      }

      if (data.userType === 'admin') {
        navigate('/admin');
      } else {
        navigate('/user');
      }
    } else {
      alert(data.message || 'Login failed');
    }
  };

  return (
    <div className="flex justify-center items-center h-[80vh]">
      <form onSubmit={handleLogin} className="bg-white p-6 rounded-lg shadow-lg w-96">
        <h2 className="text-2xl font-bold mb-4 text-center">Login</h2>

        <input
          type="text"
          placeholder="Username"
          className="w-full border p-2 mb-4 rounded"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          className="w-full border p-2 mb-4 rounded"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />

        <button type="submit" className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700">
          Login
        </button>

        <p className="mt-4 text-center">
          Don't have an account?{' '}
          <span
            className="text-blue-600 cursor-pointer"
            onClick={() => navigate('/register')}
          >
            Register here
          </span>
        </p>

        {/* Display birthday wish message if it's the user's birthday */}
        {birthdayMessage && (
          <p className="mt-4 text-center text-green-600 font-semibold">
            {birthdayMessage}
          </p>
        )}

      </form>
    </div>
  );
}

export default Login;
