import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const CustomizePackage = () => {
  const { packageId } = useParams(); // Get the packageId from the URL
  const [packageData, setPackageData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    console.log("Fetching package with ID:", packageId);
    fetch(`http://localhost:5001/packages`)
      .then((res) => res.json())
      .then((allPackages) => {
        const found = allPackages.find((p) => p._id === packageId);
        if (found) {
          setPkg(found);
        } else {
          setError('Package not found');
        }
      })
      .catch(() => setError('Failed to fetch package'));
  }, [packageId]);
  if (error) return <div>{error}</div>;
  if (!packageData) return <div>Loading...</div>;

  return (
    <div>
      <h1>Customize Package: {packageData.destination}</h1>
      <p>Price: ${packageData.price}</p>
      <p>Duration: {packageData.duration}</p>
      {/* More customization options */}
    </div>
  );
};

export default CustomizePackage;
