import React, { useEffect, useState } from 'react';

export default function DiscountSettings() {
  const [discounts, setDiscounts] = useState([]); // store all discounts
  const [inputs, setInputs] = useState({ discount: '', months: '' }); // input fields for discount
  const [message, setMessage] = useState(''); // success or error message
  const API_URL = 'http://localhost:5003/admin/discount';

  // Fetch all discounts on mount
  useEffect(() => {
    fetchDiscountSettings();
  }, []);

  const fetchDiscountSettings = async () => {
    try {
      const res = await fetch(API_URL);
      const data = await res.json();

      if (Array.isArray(data)) {
        setDiscounts(data);
      } else {
        setMessage(data.message || "No seasonal discounts available");
      }
    } catch (err) {
      setMessage("Failed to load discount settings");
      console.error("Error fetching discount settings:", err);
    }
  };

  const handleInputChange = (field, value) => {
    setInputs(prev => ({ ...prev, [field]: value }));
  };

  const handleAddDiscount = async () => {
    const { discount, months } = inputs;
    if (!discount || !months) {
      alert('Both fields are required!');
      return;
    }

    // Validate the discount and months
    if (discount < 1 || discount > 100) {
      alert('Discount should be between 1% and 100%');
      return;
    }

    const monthsArray = months.split(',').map(m => parseInt(m.trim(), 10));
    if (monthsArray.some(month => month < 1 || month > 12)) {
      alert('Months should be between 1 and 12');
      return;
    }

    const newDiscount = {
      discount: Number(discount),
      months: monthsArray,
    };

    try {
      const res = await fetch(API_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newDiscount),
      });

      if (res.ok) {
        setMessage("New seasonal discount added successfully!");
        fetchDiscountSettings(); // Refresh discount settings after adding
        setInputs({ discount: '', months: '' });
      } else {
        setMessage("Failed to add new discount");
      }
    } catch (err) {
      setMessage("Error adding new discount");
      console.error("Error adding discount:", err);
    }
  };

  const handleDeleteDiscount = async (id) => {
    try {
      const res = await fetch(`${API_URL}/${id}`, {
        method: 'DELETE',
      });

      if (res.ok) {
        setMessage("Discount deleted successfully!");
        fetchDiscountSettings(); // Refresh discount settings after delete
      } else {
        setMessage("Failed to delete discount");
      }
    } catch (err) {
      setMessage("Error deleting discount");
      console.error("Error deleting discount:", err);
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6 text-center">Discount Settings</h2>

      {/* Display current discounts */}
      <div className="mb-6">
        <h3 className="text-2xl font-bold mb-4">Existing Seasonal Discounts</h3>
        {discounts.length > 0 ? (
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white border border-gray-200 text-left">
              <thead className="bg-gray-100">
                <tr>
                  <th className="px-4 py-2 border">Discount (%)</th>
                  <th className="px-4 py-2 border">Months</th>
                  <th className="px-4 py-2 border">Actions</th>
                </tr>
              </thead>
              <tbody>
                {discounts.map((discountObj) => (
                  <tr key={discountObj._id} className="hover:bg-gray-50">
                    <td className="px-4 py-2 border">{discountObj.discount}%</td>
                    <td className="px-4 py-2 border">{discountObj.months.join(', ')}</td>
                    <td className="px-4 py-2 border">
                      <button
                        onClick={() => handleDeleteDiscount(discountObj._id)}
                        className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <p>No seasonal discounts available.</p>
        )}
      </div>

      {/* Add/Update Discount Form */}
      <div className="flex flex-wrap gap-4 mb-10 items-end">
        <input
          type="number"
          placeholder="Seasonal Discount Percentage"
          value={inputs.discount || ''}
          onChange={(e) => handleInputChange('discount', e.target.value)}
          className="border p-2 rounded w-1/5"
        />
        <input
          type="text"
          placeholder="Seasonal Discount Months (comma-separated)"
          value={inputs.months || ''}
          onChange={(e) => handleInputChange('months', e.target.value)}
          className="border p-2 rounded w-1/5"
        />
        <button
          onClick={handleAddDiscount}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Add Discount
        </button>
      </div>

      {/* Success/Error Message */}
      {message && <p className="mt-4 text-red-500">{message}</p>}
    </div>
  );
}
