import React, { useState, useEffect } from 'react';

function DiscountSettings() {
  const [months, setMonths] = useState([]); // Starts as an empty array
  const [discount, setDiscount] = useState(null); // Default null for seasonal discount
  const [message, setMessage] = useState(""); // To display success or error messages
  const [isNewDiscount, setIsNewDiscount] = useState(false); // Flag for checking if discount is new

  useEffect(() => {
    // Fetch current discount settings when the component mounts
    fetch("http://localhost:5003/admin/discount")
      .then((res) => res.json())
      .then((data) => {
        if (data.months && data.discount) {
          // If there is an existing discount, use the returned months and discount
          setMonths(data.months);
          setDiscount(data.discount);
          setIsNewDiscount(false); // Existing discount found
        } else if (data.message) {
          // If no discount is set, show the message
          setMessage(data.message); // "No seasonal discount set yet"
          setIsNewDiscount(true); // Indicate that a new discount needs to be created
        }
      })
      .catch((err) => setMessage("Failed to load current discount settings"));
  }, []);

  const handleMonthChange = (e) => {
    const inputValue = e.target.value;
    setMonths(inputValue);
  };

  const handleMonthBlur = () => {
    // On blur, clean up the input into valid months (1 to 12)
    const validMonths = months
      .split(",") // Split by commas
      .map((str) => str.trim()) // Remove extra spaces
      .filter((str) => {
        const num = parseInt(str, 10);
        return num >= 1 && num <= 12 && !isNaN(num); // Ensure number is valid
      });

    setMonths(validMonths); // Update months to valid values
  };

  const handleDiscountChange = (e) => {
    // Only allow numbers between 0 and 100 for discount
    const newDiscount = parseFloat(e.target.value);
    if (!isNaN(newDiscount) && newDiscount >= 0 && newDiscount <= 100) {
      setDiscount(newDiscount);
    }
  };

  const handleSubmit = () => {
    // Ensure months and discount are valid before submitting
    if (months.length === 0 || discount === null) {
      setMessage("Please provide valid months and discount percentage");
      return;
    }

    fetch("http://localhost:5003/admin/discount", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        months: months, // Send months array
        discount: discount, // Send discount value
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        setMessage("Discount settings updated successfully");
        setIsNewDiscount(false); // Mark as not new anymore
      })
      .catch((err) => setMessage("Failed to update discount settings"));
  };

  const handleAddDiscount = () => {
    // Add new discount settings to the backend
    if (months.length === 0 || discount === null) {
      setMessage("Please provide valid months and discount percentage");
      return;
    }

    fetch("http://localhost:5003/admin/discount", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        months: months, // Send months array
        discount: discount, // Send discount value
      }),
    })
      .then((res) => res.json())
      .then((data) => {
        setMessage("New discount added successfully");
        setIsNewDiscount(false); // Mark as not new anymore
      })
      .catch((err) => setMessage("Failed to add new discount"));
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6 text-blue-600">Discount Settings</h2>
      {isNewDiscount ? (
        <>
          <p className="mb-4 text-lg">No seasonal discount found. Add a new discount below:</p>
          <div className="mb-4">
            <label htmlFor="months" className="block text-lg font-medium">
              Seasonal Discount Months (Comma-separated):
            </label>
            <input
              type="text"
              id="months"
              value={months}
              onChange={handleMonthChange}
              onBlur={handleMonthBlur} // Trigger validation when user leaves the input field
              className="border p-2 rounded mt-1 w-full"
              placeholder="e.g. 1, 2, 3"
            />
          </div>
          <div className="mb-4">
            <label htmlFor="discount" className="block text-lg font-medium">
              Seasonal Discount Percentage:
            </label>
            <input
              type="number"
              id="discount"
              value={discount || ""}
              onChange={handleDiscountChange}
              className="border p-2 rounded mt-1 w-full"
              placeholder="e.g. 10"
            />
          </div>
          <button
            onClick={handleAddDiscount}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Add New Discount
          </button>
        </>
      ) : (
        <>
          <p className="mb-4 text-lg">Edit the current seasonal discount below:</p>
          <div className="mb-4">
            <label htmlFor="months" className="block text-lg font-medium">
              Seasonal Discount Months (Comma-separated):
            </label>
            <input
              type="text"
              id="months"
              value={months.join(", ")} // Join months array with commas
              onChange={handleMonthChange}
              onBlur={handleMonthBlur} // Trigger validation on blur
              className="border p-2 rounded mt-1 w-full"
              placeholder="e.g. 1, 2, 3"
            />
          </div>
          <div className="mb-4">
            <label htmlFor="discount" className="block text-lg font-medium">
              Seasonal Discount Percentage:
            </label>
            <input
              type="number"
              id="discount"
              value={discount || ""}
              onChange={handleDiscountChange}
              className="border p-2 rounded mt-1 w-full"
              placeholder="e.g. 10"
            />
          </div>
          <button
            onClick={handleSubmit}
            className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            Save Changes
          </button>
        </>
      )}
      {message && <p className="mt-4 text-green-600">{message}</p>}
    </div>
  );
}

export default DiscountSettings;
