import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const CustomizePackage = () => {
  const { packageId } = useParams(); // Get the packageId from the URL
  const [packageData, setPackageData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://localhost:5001/packages/${pkgId}') // Send GET request to backend
      .then((res) => res.json())
      .then((data) => setPackageData(data))
      .catch((err) => setError('Failed to load package details'));
  }, [packageId]); // Re-fetch when packageId changes

  if (error) return <div>{error}</div>;
  if (!packageData) return <div>Loading...</div>;

  return (
    <div>
      <h1>Customize Package: {packageData.destination}</h1>
      <p>Price: ${packageData.price}</p>
      <p>Duration: {packageData.duration}</p>
      {/* More customization options */}
    </div>
  );
};

export default CustomizePackage;
