import React, { useEffect, useState } from 'react';

export default function Admin() {
  const [packages, setPackages] = useState([]);
  const [formData, setFormData] = useState({
    destination: '',
    price: '',
    duration: '',
    activities: ''
  });

  // Fetch all packages
  const fetchPackages = async () => {
    const res = await fetch('http://localhost:5001/packages');
    const data = await res.json();
    setPackages(data);
  };

  useEffect(() => {
    fetchPackages();
  }, []);

  // Handle input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Add new package
  const handleAddPackage = async (e) => {
    e.preventDefault();

    const res = await fetch('http://localhost:5001/packages', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ...formData, activities: formData.activities.split(',').map(a => a.trim()) })
    });

    const data = await res.json();
    if (res.ok) {
      alert('Package added!');
      setFormData({ destination: '', price: '', duration: '', activities: '' });
      fetchPackages(); // Refresh list
    } else {
      alert(data.error || 'Failed to add package');
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">ðŸ§³ Admin Dashboard - Holiday Packages</h2>

      {/* Add Package Form */}
      <form onSubmit={handleAddPackage} className="mb-8 bg-white p-4 rounded shadow-md w-full max-w-md">
        <input name="destination" placeholder="Destination" value={formData.destination} onChange={handleChange} className="w-full p-2 mb-2 border rounded" required />
        <input name="price" placeholder="Price" value={formData.price} onChange={handleChange} className="w-full p-2 mb-2 border rounded" required />
        <input name="duration" placeholder="Duration (in days)" value={formData.duration} onChange={handleChange} className="w-full p-2 mb-2 border rounded" required />
        <input name="activities" placeholder="Activities (comma-separated)" value={formData.activities} onChange={handleChange} className="w-full p-2 mb-2 border rounded" required />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">Add Package</button>
      </form>

      {/* Package List */}
      <div>
        <h3 className="text-xl font-semibold mb-2">All Packages:</h3>
        <ul className="space-y-2">
          {packages.map((pkg) => (
            <li key={pkg._id} className="bg-white p-4 rounded shadow">
              <strong>{pkg.destination}</strong> â€” {pkg.duration} days â€” â‚¹{pkg.price}
              <br />
              <em>Activities: {pkg.activities.join(', ')}</em>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
