import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function CustomizePackage() {
  const { packageId } = useParams(); // Get package ID from URL params
  const [packageData, setPackageData] = useState(null);
  const [customActivities, setCustomActivities] = useState('');
  const [customDates, setCustomDates] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`http://localhost:5001/packages/${packageId}`)
      .then((res) => res.json())
      .then((data) => setPackageData(data))
      .catch((err) => alert('Failed to load package'));
  }, [packageId]);

  const handleSave = () => {
    const customData = {
      packageId,
      customActivities: customActivities.split(',').map((activity) => activity.trim()),
      customDates
    };

    // This would normally involve sending data to the backend for processing
    // For now, we’ll just log it to simulate a save
    console.log(customData);

    navigate('/user'); // Redirect back to user home after saving
  };

  if (!packageData) return <div>Loading package...</div>;

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6">Customize Your Package</h2>

      <div className="mb-4">
        <strong>Destination:</strong> {packageData.destination}
      </div>
      <div className="mb-4">
        <strong>Price:</strong> ${packageData.price}
      </div>
      <div className="mb-4">
        <strong>Duration:</strong> {packageData.duration} days
      </div>

      <div className="mb-6">
        <strong>Activities:</strong>
        <div>{packageData.activities.join(', ')}</div>
        <input
          type="text"
          placeholder="Add custom activities (comma separated)"
          value={customActivities}
          onChange={(e) => setCustomActivities(e.target.value)}
          className="w-full mt-2 p-2 border border-gray-300 rounded"
        />
      </div>

      <div className="mb-6">
        <strong>Custom Dates:</strong>
        <input
          type="text"
          placeholder="Enter custom dates"
          value={customDates}
          onChange={(e) => setCustomDates(e.target.value)}
          className="w-full mt-2 p-2 border border-gray-300 rounded"
        />
      </div>

      <button
        onClick={handleSave}
        className="bg-blue-600 text-white px-6 py-2 rounded-full hover:bg-blue-700"
      >
        Save Customization
      </button>
    </div>
  );
}

export default CustomizePackage;
