// src/pages/Admin.js
import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function Admin() {
  const navigate = useNavigate();

  console.log("Admin page loaded");

  return (
    <div className="min-h-screen bg-gradient-to-r from-gray-200 to-gray-400 text-black py-20 px-6">
      <div className="max-w-xl mx-auto text-center">
        <h2 className="text-4xl font-bold mb-4">üë©‚Äçüíº Welcome, Admin!</h2>
        <p className="text-lg mb-8">Manage packages, view analytics, and more.</p>

        <div className="space-y-4">
          <button
            onClick={() => navigate('/admin/add-package')}
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 px-6 rounded-lg"
          >
            ‚ûï Add New Package
          </button>

          <button
            onClick={() => navigate('/admin/view-packages')}
            className="w-full bg-green-600 hover:bg-green-700 text-white font-semibold py-3 px-6 rounded-lg"
          >
            üì¶ View All Packages
          </button>

          {/* You can add more buttons later like Edit/Delete Packages, Analytics, etc. */}
        </div>
      </div>
    </div>
  );
}
