import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Packages() {
  const [packages, setPackages] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5005/packages')
      .then(response => {
        setPackages(response.data);
      })
      .catch(error => {
        console.error('Error fetching packages:', error);
      });
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">ðŸŽ’ Holiday Packages</h2>
      {packages.length === 0 ? (
        <p>No packages found.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {packages.map(pkg => (
            <div key={pkg._id} className="bg-white shadow rounded-xl p-4">
              <h3 className="text-xl font-semibold text-blue-600">{pkg.destination}</h3>
              <p><strong>Price:</strong> â‚¹{pkg.price}</p>
              <p><strong>Duration:</strong> {pkg.duration}</p>
              <p><strong>Activities:</strong> {pkg.activities.join(', ')}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
