import React from 'react';

function App() {
  return (
    <div className="min-h-screen bg-gray-100 text-gray-800">
      {/* Navbar */}
      <header className="bg-white shadow p-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-blue-600">MMT Clone</h1>
        <nav className="space-x-4">
          <a href="#" className="text-gray-600 hover:text-blue-600">Home</a>
          <a href="#" className="text-gray-600 hover:text-blue-600">Packages</a>
          <a href="#" className="text-gray-600 hover:text-blue-600">Admin</a>
        </nav>
      </header>

      {/* Hero Section */}
      <section className="text-center py-20 bg-gradient-to-r from-blue-200 to-blue-400 text-white">
        <h2 className="text-4xl font-bold mb-4">Plan Your Dream Vacation ✈️</h2>
        <p className="text-lg mb-6">Custom packages, discounts, and eco-friendly travel—all in one place.</p>
        <button className="bg-white text-blue-600 px-6 py-2 rounded-full font-semibold hover:bg-gray-100">
          Explore Now
        </button>
      </section>

      {/* Features / Cards */}
      <section className="grid grid-cols-1 md:grid-cols-3 gap-6 p-6">
        {['Plan a Trip', 'Check Discounts', 'Recommendations'].map((title, index) => (
          <div key={index} className="bg-white p-6 rounded-xl shadow hover:shadow-lg transition-all">
            <h3 className="text-xl font-semibold mb-2">{title}</h3>
            <p className="text-gray-600">Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
            <button className="mt-4 text-blue-600 hover:underline">Go</button>
          </div>
        ))}
      </section>

      {/* Footer */}
      <footer className="text-center py-4 text-sm text-gray-500 border-t mt-10">
        © 2025 MakeMyTrip Clone – Built with ❤️
      </footer>
    </div>
  );
}

export default App;
