// src/pages/ViewPackages.js
import React, { useEffect, useState } from 'react';

export default function ViewPackages() {
  const [packages, setPackages] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:5001/packages')
      .then((res) => res.json())
      .then((data) => {
        setPackages(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching packages:', err);
        setLoading(false);
      });
  }, []);

  return (
    <div className="p-8">
      <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">ðŸ“¦ All Holiday Packages</h2>

      {loading ? (
        <p className="text-center">Loading packages...</p>
      ) : packages.length === 0 ? (
        <p className="text-center text-gray-600">No packages available.</p>
      ) : (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {packages.map((pkg) => (
            <div
              key={pkg._id}
              className="bg-white rounded-lg shadow-md p-6 hover:shadow-xl transition-shadow"
            >
              <h3 className="text-xl font-semibold text-blue-600 mb-2">{pkg.destination}</h3>
              <p className="text-sm text-gray-600 mb-1">Duration: {pkg.duration} days</p>
              <p className="text-sm text-gray-600 mb-1">Price: â‚¹{pkg.price}</p>
              <p className="text-sm text-gray-600 mb-2">
                Activities: {pkg.activities?.join(', ') || 'N/A'}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
